import { useState } from 'react'
import { DivEtiqueta } from '../styled'
import { useForm } from 'react-hook-form'
import { yupResolveer } from '@hookform/resolvers/yup'
import * as yup from 'yup'

const schema = yup.object({
    nome: yup.string().required("O nome é obrigatório"),
    email: yup.string().email("Digite um e-mail válido!").required("O e-mail é obrigatório"),
    cpf: yup.string().min(11,"O CPF deve ter pelo menos 11 digitos").required("O CPF é obrigatório")
}).required()


export default function EtiquetasHookForm(){

    const {register, handleSubmit, formState:{errors}} = useForm(
        {resolver: yupResolveer()}
    )
    const [listaCliente, setListaCliente] = useState([])

    function inserirCliente(cliente){
        setListaCliente([...listaCliente, cliente])
    }

    return(
        <DivEtiqueta>
            <form onSubmit={handleSubmit(inserirCliente)}>
                <fieldset>
                    <legend>Dados Pessoais</legend>
                    <label>Nome:
                        <input type="text" {...register('nome')} />
                    </label>
                    <label>E-mail:
                        <input type="text" {...register('email')}/>
                    </label>
                    <label>CPF:
                        <input type="text" {...register('cpf')} />
                    </label>
                    <button type='submit'>Criar</button>
                </fieldset>
            </form>
            <div className="painel">
                {
                    listaCliente.map((cli, index)=>                    
                <div className="etiqueta" key={index}>
                    <p>Nome: {cli.nome}</p>
                    <p>E-mail: {cli.email}</p>
                    <p>CPF: {cli.cpf}</p>
                </div>
                    )
                }
            </div>
        </DivEtiqueta>
    )
}
